$("main").show(2000);

$(function () {
  $("h2,h3").on("click", function () {
    $(this).parents(".row").next().toggle(1000);
  });

  events.forEach((event) => {
    $("#stoleti tbody").append(`<tr>
        <td class="event-year"><b>${event.století}</b></td>
        <td>
          <p class="event-století"><i class="fas fa-plus ikonka"></i></p>
          <p class="event-popis">${event.popis}</p>
        </td>    

    </tr>`);
  });

  $(".event-popis").hide();
  $(".event-století i, .event-století a").on("mouseover", function () {
    $(".event-popis").hide();
    $(this).parent().next().show(1000);
  });
})

heroes.forEach((hero) => {
  $("#postavy .list-group").append(`<li class="list-group-item list-group-item-action list-group-item-danger">${hero.name}</li>`);
});

function fillPersonCard(person) {
  let hero = heroes.find(item => { return item.name === person });
  $(".card-header").html(`<i class="fas fa-star-of-life"></i> <b>${hero.birth}</b> - <i class="fas fa-cross"></i> <b>${hero.death}</b>`);
  $(".card-title").text(hero.name);
  $(".card-text").text(hero.biography);
  $(".card-footer").html(`Odkaz: <a href="${hero.online}">${hero.online}</a>`);
  $(".gallery").empty();
  $(".card-text").append(`<br><br><div><a href="${hero.odkaz}" target="_blank"><img src="../IMG/${hero.portraits}" alt="" class="img-fluid portret"></a></div>`);
}

$("#postavy li:first").addClass('active');
fillPersonCard(heroes[0].name);
$("#postavy li").on("click", function () {
  $("#postavy li").removeClass("active");
  $(this).addClass("active");
  let person = $(this).text();
  $("#portret").slideUp(500, function () {
    fillPersonCard(person);
  });
  $("#portret").slideDown(500);
});

pamatky.forEach((pamatka) => {
  $("#pamatkyId").append(`    
  <div class="col-sm-6 mt-3 pb-3 border-bottom">
    <article>
      <figure>
        <img src="../IMG/${pamatka.image}" alt="${pamatka.title}" class="img-fluid obr">
      </figure>
      <h3>${pamatka.title}</h3>
      <div class="pamatky-text">
          <p>${pamatka.text}</p>
          <p><a href="${pamatka.odkaz}" target="_blank">Více informací</a></p>
      </div>
      </div>
    </article>
  </div>        
`);
});


$(".pamatky-text").hide();

$("#pamatkyId h3").on("click", function () {
  $(this).next(".pamatky-text").toggle(1000);
});

